#include <machine.h>
#include <time.h>

static char *str = 
"";
void shell8(void)
{
    unsigned long start_count = 0;
    unsigned long stop_count = 0;
    unsigned long total_count = 0;
    int i, err;

    err=0;
    printf("sha test begin.\n");
    reset_perf_counters();
    start_count = get_count();
    if(SIMU_FLAG){
        err = sha_driver(str);
    }else{
        for(i=0; i<LOOPTIMES; i++)
            err += sha_driver(str);
    }
    stop_count = get_count();
    total_count = stop_count - start_count;
    print_perf_counters();
	if(err == 0){
        printf("sha PASS!\n");
	}else{
        printf("sha ERROR!!!\n");
	}

	printf("sha: Total Count = 0x%x\n", total_count);

    return;
}
